import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function StudentReportView() {
    const { studentId } = useParams();
    const [reportData, setReportData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [selectedFaculty, setSelectedFaculty] = useState('');

    useEffect(() => {
        async function fetchData() {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get(
                    `http://localhost:5000/api/student-report/${studentId}`,
                    { headers: { Authorization: `Bearer ${token}` } }
                );
                setReportData(response.data);
            } catch (error) {
                console.error('Error fetching data:', error);
                setReportData(null);
            } finally {
                setLoading(false);
            }
        }
        fetchData();
    }, [studentId]);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (!reportData || !reportData.feedback || reportData.feedback.length === 0) {
        return <div>No feedback available</div>;
    }

    console.log('Report Data:', reportData);

    // Group feedback by faculty and subject
    const groupedFeedback = reportData.feedback.reduce((acc, item) => {
        const key = `${item.facultyName} - ${item.subject}`;
        if (!acc[key]) {
            acc[key] = [];
        }
        acc[key].push(item);
        return acc;
    }, {});

    const filteredFeedback = selectedFaculty
        ? Object.entries(groupedFeedback).filter(([key]) => key.startsWith(selectedFaculty))
        : Object.entries(groupedFeedback);

    return (
        <div style={{ padding: '20px' }}>
            <h1>Student Feedback Report for {reportData.studentName}</h1>
            <p>USN: {reportData.usn}</p>
            <p>Course: {reportData.course}</p>
            <p>Semester: {reportData.semester}</p>
            <p>Feedback Type: {reportData.feedbackType || 'N/A'}</p>
            <div style={{ marginBottom: '20px' }}>
                <label htmlFor="faculty-select">Select Faculty: </label>
                <select
                    id="faculty-select"
                    value={selectedFaculty}
                    onChange={(e) => setSelectedFaculty(e.target.value)}
                >
                    <option value="">All</option>
                    {Array.from(new Set(reportData.feedback.map((item) => item.facultyName))).map((faculty) => (
                        <option key={faculty} value={faculty}>
                            {faculty}
                        </option>
                    ))}
                </select>
            </div>
            {filteredFeedback.length > 0 ? (
                filteredFeedback.map(([key, feedbacks]) => (
                    <div key={key} style={{ marginBottom: '30px' }}>
                        <h2 style={{ color: '#2c3e50', marginBottom: '15px' }}>{key}</h2>
                        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                            <thead>
                                <tr>
                                    <th style={{ border: '1px solid #ddd', padding: '12px', backgroundColor: '#f8f9fa' }}>Question</th>
                                    <th style={{ border: '1px solid #ddd', padding: '12px', backgroundColor: '#f8f9fa' }}>Score</th>
                                    <th style={{ border: '1px solid #ddd', padding: '12px', backgroundColor: '#f8f9fa' }}>Comment</th>
                                </tr>
                            </thead>
                            <tbody>
                                {feedbacks.map((feedback, index) => (
                                    <tr key={index}>
                                        <td style={{ border: '1px solid #ddd', padding: '12px' }}>{feedback.question}</td>
                                        <td style={{ border: '1px solid #ddd', padding: '12px', textAlign: 'center' }}>{feedback.score}</td>
                                        <td style={{ border: '1px solid #ddd', padding: '12px' }}>{feedback.comment || 'No comment provided'}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                ))
            ) : (
                <div>No feedback available</div>
            )}
        </div>
    );
}

export default StudentReportView;
