import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import jsPDF from 'jspdf';
import './Admin.css';

const Admin = () => {
    const navigate = useNavigate();
    const [faculties, setFaculties] = useState([]);
    const [selectedFaculty, setSelectedFaculty] = useState('');

    useEffect(() => {
        const fetchFaculties = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get('http://localhost:5000/api/courses', {
                    headers: { Authorization: `Bearer ${token}` },
                });

                // Split elective faculties into separate entries
                const separatedFaculties = response.data.flatMap((faculty) =>
                    faculty.facultyName.includes('/')
                        ? faculty.facultyName.split('/').map((name) => ({
                              ...faculty,
                              facultyName: name.trim(),
                          }))
                        : [faculty]
                );

                setFaculties(separatedFaculties);
            } catch (err) {
                console.error('Error fetching faculties:', err);
            }
        };
        fetchFaculties();
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');
        navigate('/');
    };

    const downloadFacultyReport = async () => {
        if (!selectedFaculty) {
            alert('Please select a faculty to download the report.');
            return;
        }

        try {
            const token = localStorage.getItem('token');
            const response = await axios.get('http://localhost:5000/api/feedback', {
                headers: { Authorization: `Bearer ${token}` },
            });

            // Find the selected faculty directly
            const feedback = response.data.find((entry) => entry.facultyName === selectedFaculty);

            if (!feedback) {
                alert('No feedback data available for the selected faculty.');
                return;
            }

            const doc = new jsPDF();
            doc.setFontSize(16);
            doc.text(`Faculty Report: ${feedback.facultyName}`, 10, 10);
            doc.setFontSize(12);
            doc.text(`Cumulative Average Marks: ${feedback.averageScore.toFixed(2)} / 100`, 10, 20);

            feedback.scores.forEach((scoreSet, index) => {
                scoreSet.forEach((score, qIndex) => {
                    doc.text(`Q${qIndex + 1}: ${score}`, 10, 30 + index * 10 + qIndex * 10);
                });
            });

            doc.save(`${feedback.facultyName}_Report.pdf`);
        } catch (err) {
            console.error('Error generating report:', err);
            alert('Failed to generate the report. Please try again.');
        }
    };

    return (
        <div className="admin-container">
            <h2>Admin Dashboard</h2>
            <div className="admin-options">
                <Link to="/admin/add-courses" className="admin-button">Add Courses</Link>
                <Link to="/admin/add-students" className="admin-button">Add Students</Link>
                <Link to="/admin/feedback-viewer" className="admin-button">View Feedback</Link>
                <div>
                    <select
                        value={selectedFaculty}
                        onChange={(e) => setSelectedFaculty(e.target.value)}
                        className="admin-select"
                    >
                        <option value="">Select Faculty</option>
                        {faculties.map((faculty) => (
                            <option key={faculty._id} value={faculty._id}>
                                {faculty.facultyName}
                            </option>
                        ))}
                    </select>
                    <button onClick={downloadFacultyReport} className="admin-button">
                        Download Selected Faculty Report
                    </button>
                </div>
                <button onClick={handleLogout} className="logout-button">Logout</button>
            </div>
        </div>
    );
};

export default Admin;
