import React, { useState, useEffect } from 'react';
import axios from 'axios';

const StudentReports = () => {
    const [students, setStudents] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchStudents = async () => {
            try {
                const token = localStorage.getItem('token'); // Retrieve the token from localStorage
                const response = await axios.get('http://localhost:5000/api/students', {
                    headers: { Authorization: `Bearer ${token}` }, // Include the token in the request headers
                });
                setStudents(response.data);
            } catch (err) {
                console.error('Error fetching students:', err);
                setError(err.response?.data?.message || 'Failed to fetch student data.');
            }
        };

        fetchStudents();
    }, []);

    const downloadStudentReport = async (studentId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get(`http://localhost:5000/api/student-report/${studentId}`, {
                responseType: 'blob',
                headers: { Authorization: `Bearer ${token}` },
            });

            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download', `student_report_${studentId}.pdf`);
            document.body.appendChild(link);
            link.click();
            link.remove();
        } catch (err) {
            console.error('Error downloading report:', err);
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <h1>Student Reports</h1>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px' }}>
                <thead>
                    <tr>
                        <th style={{ border: '1px solid #ddd', padding: '8px' }}>Student ID</th>
                        <th style={{ border: '1px solid #ddd', padding: '8px' }}>Student Name</th>
                        <th style={{ border: '1px solid #ddd', padding: '8px' }}>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {students.map((student) => (
                        <tr key={student.studentId}>
                            <td style={{ border: '1px solid #ddd', padding: '8px' }}>{student.studentId}</td>
                            <td style={{ border: '1px solid #ddd', padding: '8px' }}>{student.studentName}</td>
                            <td style={{ border: '1px solid #ddd', padding: '8px' }}>
                                <button
                                    onClick={() => downloadStudentReport(student.studentId)}
                                    style={{
                                        padding: '5px 10px',
                                        backgroundColor: '#007bff',
                                        color: '#fff',
                                        border: 'none',
                                        borderRadius: '3px',
                                        cursor: 'pointer',
                                    }}
                                >
                                    Download Report
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default StudentReports;
