import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { ClipLoader } from 'react-spinners';
import './FeedbackLogin.css';

const FeedbackLogin = () => {
    const [studentId, setStudentId] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            console.log('Attempting login with:', { studentId, password });
            const response = await axios.post('http://localhost:5000/api/auth/login', {
                studentId,
                password,
            });

            console.log('Login response:', response.data);
            const { token, role } = response.data;
            localStorage.setItem('token', token);

            if (role === 'student') {
                console.log('Redirecting to /feedback for student');
                navigate('/feedback', { replace: true });
            } else if (role === 'admin') {
                console.log('Redirecting to /admin for admin');
                navigate('/admin', { replace: true });
            } else {
                setError('Invalid role. Please contact support.');
                console.log('Invalid role:', role);
            }
        } catch (err) {
            const errorMessage = err.response?.data?.message || 'Invalid credentials. Please try again.';
            setError(errorMessage);
            console.error('Login error:', err.response || err);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="login-container">
            <div className="login-box">
                <img src="/amity-logo.png" alt="Amity University Logo" className="logo" />
                <h2>Student Login</h2>
                <form onSubmit={handleLogin}>
                    <div className="form-group">
                        <label htmlFor="studentId">Student ID</label>
                        <input
                            type="text"
                            id="studentId"
                            value={studentId}
                            onChange={(e) => setStudentId(e.target.value)}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Password</label>
                        <input
                            type="password"
                            id="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                    {error && <p className="error">{error}</p>}
                    <button type="submit" disabled={loading}>
                        {loading ? <ClipLoader size={20} color="#fff" /> : 'Login'}
                    </button>
                </form>
            </div>
        </div>
    );
};

export default FeedbackLogin;
