import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function StudentReportView() {
    const { studentId } = useParams();
    const [reportData, setReportData] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        async function fetchData() {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get(
                    `http://localhost:5000/api/student-report/${studentId}`,
                    { headers: { Authorization: `Bearer ${token}` } }
                );
                setReportData(response.data);
            } catch (error) {
                console.error('Error fetching data:', error);
                setReportData(null);
            } finally {
                setLoading(false);
            }
        }
        fetchData();
    }, [studentId]);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (!reportData || !reportData.feedback || reportData.feedback.length === 0) {
        return <div>No feedback available</div>;
    }

    console.log('Report Data:', reportData);

    return (
        <div style={{ padding: '20px' }}>
            <h1>Student Feedback Report for {reportData.studentName}</h1>
            <p>USN: {reportData.usn}</p>
            <p>Course: {reportData.course}</p>
            <p>Semester: {reportData.semester}</p>
            {reportData.feedback.map((facultyFeedback, index) => (
                <div key={index} style={{ marginBottom: '30px' }}>
                    <h2 style={{ color: '#2c3e50', marginBottom: '15px' }}>{facultyFeedback.facultyDetails}</h2>
                    <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                        <thead>
                            <tr>
                                <th style={{ border: '1px solid #ddd', padding: '12px', backgroundColor: '#f8f9fa' }}>Question</th>
                                <th style={{ border: '1px solid #ddd', padding: '12px', backgroundColor: '#f8f9fa' }}>Score</th>
                                <th style={{ border: '1px solid #ddd', padding: '12px', backgroundColor: '#f8f9fa' }}>Comment</th>
                            </tr>
                        </thead>
                        <tbody>
                            {facultyFeedback.feedbackItems.map((item, idx) => (
                                <tr key={idx}>
                                    <td style={{ border: '1px solid #ddd', padding: '12px' }}>{item.question}</td>
                                    <td style={{ border: '1px solid #ddd', padding: '12px', textAlign: 'center' }}>{item.score}</td>
                                    <td style={{ border: '1px solid #ddd', padding: '12px' }}>{item.comment || 'No comment provided'}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            ))}
        </div>
    );
}

export default StudentReportView;
