import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function StudentReportView() {
    const { studentId } = useParams();
    const [reportData, setReportData] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        async function fetchData() {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get(
                    `http://localhost:5000/api/student-report/${studentId}`,
                    { headers: { Authorization: `Bearer ${token}` } }
                );
                console.log('API Response:', response.data);
                setReportData(response.data);
            } catch (error) {
                console.error('Error fetching data:', error);
                setReportData(null);
            } finally {
                setLoading(false);
            }
        }
        fetchData();
    }, [studentId]);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (!reportData || !reportData.feedback || reportData.feedback.length === 0) {
        return <div>No feedback available</div>;
    }

    const feedbackType = reportData.feedbackType;

    // Group feedback by faculty and subject, keeping only questions for the selected feedback type
    const groupedFeedback = reportData.feedback
        .filter(item => item.feedbackType === feedbackType)
        .reduce((acc, item) => {
            const key = `${item.facultyName} - ${item.subject}`;
            if (!acc[key]) {
                acc[key] = [];
            }
            acc[key].push(item);
            return acc;
        }, {});

    return (
        <div style={{ padding: '20px' }}>
            <div style={{ marginBottom: '20px', backgroundColor: '#f8f9fa', padding: '15px', borderRadius: '5px' }}>
                <h1>Student Feedback Report</h1>
                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '10px' }}>
                    <p><strong>Name:</strong> {reportData.studentName}</p>
                    <p><strong>USN:</strong> {reportData.usn}</p>
                    <p><strong>Course:</strong> {reportData.course}</p>
                    <p><strong>Semester:</strong> {reportData.semester}</p>
                </div>
                <p style={{ 
                    marginTop: '10px', 
                    padding: '5px 10px',
                    backgroundColor: feedbackType === 'Pre-Feedback' ? '#e3f2fd' : '#fff3e0',
                    color: feedbackType === 'Pre-Feedback' ? '#1976d2' : '#f57c00',
                    borderRadius: '4px',
                    display: 'inline-block'
                }}>
                    <strong>Feedback Type:</strong> {feedbackType}
                </p>
            </div>

            {Object.keys(groupedFeedback).length > 0 ? (
                Object.entries(groupedFeedback).map(([key, feedbacks]) => (
                    <div key={key} style={{ marginBottom: '30px' }}>
                        <h2 style={{ color: '#2c3e50', marginBottom: '15px' }}>{key}</h2>
                        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                            <thead>
                                <tr>
                                    <th style={{ border: '1px solid #ddd', padding: '12px', backgroundColor: '#f8f9fa' }}>Question</th>
                                    <th style={{ border: '1px solid #ddd', padding: '12px', backgroundColor: '#f8f9fa' }}>Score</th>
                                    <th style={{ border: '1px solid #ddd', padding: '12px', backgroundColor: '#f8f9fa' }}>Comment</th>
                                </tr>
                            </thead>
                            <tbody>
                                {feedbacks.map((feedback, index) => (
                                    <tr key={index}>
                                        <td style={{ border: '1px solid #ddd', padding: '12px' }}>{feedback.question}</td>
                                        <td style={{ border: '1px solid #ddd', padding: '12px', textAlign: 'center' }}>{feedback.score}</td>
                                        <td style={{ border: '1px solid #ddd', padding: '12px' }}>{feedback.comment || 'No comment provided'}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                ))
            ) : (
                <div>No feedback available</div>
            )}
        </div>
    );
}

export default StudentReportView;
