import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const StudentReportView = () => {
    const { studentId } = useParams(); // Get the studentId from the URL
    const [reportData, setReportData] = useState(null);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchStudentReport = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get(`http://localhost:5000/api/student-report/${studentId}`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setReportData(response.data);
            } catch (err) {
                console.error('Error fetching student report:', err);
                setError(err.response?.data?.message || 'Failed to fetch student report.');
            }
        };

        fetchStudentReport();
    }, [studentId]);

    if (error) {
        return <p style={{ color: 'red' }}>{error}</p>;
    }

    if (!reportData) {
        return <p>Loading...</p>;
    }

    return (
        <div style={{ padding: '20px' }}>
            <h1>Student Report for {reportData.studentName}</h1>
            <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px' }}>
                <thead>
                    <tr>
                        <th style={{ border: '1px solid #ddd', padding: '8px' }}>Questions</th>
                        {reportData.faculties.map((faculty, index) => (
                            <th key={index} style={{ border: '1px solid #ddd', padding: '8px' }}>
                                {faculty}
                            </th>
                        ))}
                    </tr>
                </thead>
                <tbody>
                    {reportData.questions.map((question, index) => (
                        <tr key={index}>
                            <td style={{ border: '1px solid #ddd', padding: '8px' }}>{question.text}</td>
                            {question.scores.map((score, idx) => (
                                <td key={idx} style={{ border: '1px solid #ddd', padding: '8px' }}>
                                    {score}
                                </td>
                            ))}
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default StudentReportView;
