import React, { useEffect, useState } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Header from './components/Header/Header'; // Import the Header component
import FeedbackLogin from './components/Login/FeedbackLogin';
import FeedbackForm from './components/Feedback/FeedbackForm';
import FeedbackSubmitted from './components/FeedbackSubmitted/FeedbackSubmitted';
import Admin from './components/Admin/Admin';
import AddCourseData from './components/Admin/AddCourseData';
import AddStudentData from './components/Admin/AddStudentData';
import Footer from './components/Footer/Footer';
import FeedbackViewer from './components/Admin/FeedbackViewer';
import './App.css';

function App() {
    const [footerVisible, setFooterVisible] = useState(false);

    useEffect(() => {
        const handleScroll = () => {
            const scrollTop = window.scrollY;
            const windowHeight = window.innerHeight;
            const documentHeight = document.documentElement.scrollHeight;

            // Show footer only when scrolled near the bottom
            if (scrollTop + windowHeight >= documentHeight - 50) {
                setFooterVisible(true);
            } else {
                setFooterVisible(false);
            }
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    return (
        <BrowserRouter>
            <div className="app-container">
                <Routes>
                    <Route path="/" element={<FeedbackLogin />} /> {/* No Header for login */}
                    <Route
                        path="/feedback"
                        element={
                            <>
                                <Header /> {/* Add Header after login */}
                                <FeedbackForm />
                            </>
                        }
                    />
                    <Route
                        path="/feedback-submitted"
                        element={
                            <>
                                <Header /> {/* Add Header after login */}
                                <FeedbackSubmitted />
                            </>
                        }
                    />
                    <Route
                        path="/admin"
                        element={
                            <>
                                <Header /> {/* Add Header after login */}
                                <Admin />
                            </>
                        }
                    />
                    <Route
                        path="/admin/add-courses"
                        element={
                            <>
                                <Header /> {/* Add Header after login */}
                                <AddCourseData />
                            </>
                        }
                    />
                    <Route
                        path="/admin/add-students"
                        element={
                            <>
                                <Header /> {/* Add Header after login */}
                                <AddStudentData />
                            </>
                        }
                    />
                    <Route
                        path="/admin/feedback-viewer"
                        element={
                            <>
                                <Header /> {/* Add Header after login */}
                                <FeedbackViewer />
                            </>
                        }
                    />
                    <Route
                        path="*"
                        element={
                            <>
                                <Header /> {/* Add Header after login */}
                                <div>404 - Page Not Found</div>
                            </>
                        }
                    />
                </Routes>
                <footer className={`footer ${footerVisible ? 'visible' : ''}`}>
                    Designed by: Sachin © | Sharon Melvin | Aditya Banerjee
                </footer>
            </div>
        </BrowserRouter>
    );
}

export default App;
