import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './FeedbackViewer.css';

const FeedbackViewer = () => {
    const [feedbackData, setFeedbackData] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchFeedback = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get('http://localhost:5000/api/feedback/admin/feedback', {
                    headers: { Authorization: `Bearer ${token}` },
                });

                if (response.data.success) {
                    const processedData = response.data.data.reduce((acc, item) => {
                        const key = `${item.student_id}-${item.faculty_id}-${item.feedbackType}`;
                        if (!acc[key]) {
                            acc[key] = {
                                studentId: item.student_id,
                                studentName: item.student_name,
                                facultyName: item.faculty_name,
                                subjectName: item.subjectName,
                                feedbackType: item.feedbackType,
                                semester: item.semester,
                                courseName: item.courseName,
                                scores: [],
                                comments: item.comment
                            };
                        }
                        acc[key].scores.push({
                            questionId: item.question_id,
                            score: item.score,
                            questionText: item.question_text
                        });
                        return acc;
                    }, {});

                    setFeedbackData(Object.values(processedData));
                }
            } catch (err) {
                setError('Failed to load feedback data');
            }
        };

        fetchFeedback();
    }, []);

    return (
        <div className="feedback-viewer-container">
            <h2>Feedback Viewer</h2>
            {error && <p className="error">{error}</p>}
            {feedbackData.length === 0 ? (
                <p>No feedback available.</p>
            ) : (
                <table>
                    <thead>
                        <tr>
                            <th>Student ID</th>
                            <th>Student Name</th>
                            <th>Faculty Name</th>
                            <th>Subject Name</th>
                            <th>Feedback Type</th>
                            <th>Semester</th>
                            <th>Course Name</th>
                            <th>Scores</th>
                            <th>Comments</th>
                        </tr>
                    </thead>
                    <tbody>
                        {feedbackData.map((data, index) => (
                            <tr key={index}>
                                <td>{data.studentId}</td>
                                <td>{data.studentName}</td>
                                <td>{data.facultyName}</td>
                                <td>{data.subjectName}</td>
                                <td>{data.feedbackType}</td>
                                <td>{data.semester}</td>
                                <td>{data.courseName}</td>
                                <td>
                                    {data.scores.map((score, idx) => (
                                        <div key={idx}>
                                            <strong>Q{score.questionId}:</strong> {score.questionText} - {score.score}
                                        </div>
                                    ))}
                                </td>
                                <td>{data.comments}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default FeedbackViewer;
