import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './FeedbackViewer.css';

const FeedbackViewer = () => {
    const [feedbackData, setFeedbackData] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchFeedback = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get('http://localhost:5000/api/feedback', {
                    headers: { Authorization: `Bearer ${token}` },
                });

                // Use the backend's flattened feedback directly
                setFeedbackData(response.data);
            } catch (err) {
                setError('Failed to load feedback data. Please try again.');
            }
        };

        fetchFeedback();
    }, []);

    return (
        <div className="feedback-viewer-container">
            <h2>Feedback Viewer</h2>
            {error && <p className="error">{error}</p>}
            {feedbackData.length === 0 ? (
                <p>No feedback available.</p>
            ) : (
                <table>
                    <thead>
                        <tr>
                            <th>Faculty Name</th>
                            <th>Average Score</th>
                        </tr>
                    </thead>
                    <tbody>
                        {feedbackData.map((data) => (
                            <tr key={data.facultyName}>
                                <td>{data.facultyName}</td>
                                <td>{data.averageScore.toFixed(2)}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default FeedbackViewer;
