import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { ClipLoader } from 'react-spinners';
import './FeedbackForm.css';

const FeedbackForm = () => {
    const [faculties, setFaculties] = useState([]);
    const [feedback, setFeedback] = useState({});
    const [questions, setQuestions] = useState([]);
    const [feedbackType, setFeedbackType] = useState(null); // Initially null
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [selectedElectiveFaculty, setSelectedElectiveFaculty] = useState({}); // Track selected elective faculties
    const [activeFaculty, setActiveFaculty] = useState(null); // Track the currently selected faculty
    const navigate = useNavigate();

    const electiveFaculties = React.useMemo(() => ["Dr. Raksha", "Dr. Shreya", "Dr. Mishbah Hasan"], []);

    useEffect(() => {
        if (!feedbackType) return; // Fetch questions only after feedbackType is selected

        const fetchQuestions = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    setError('Authentication token is missing. Redirecting to login...');
                    setTimeout(() => navigate('/'), 2000);
                    return;
                }

                const response = await axios.get(
                    `http://localhost:5000/api/feedback/questions/${feedbackType}`,
                    { headers: { Authorization: `Bearer ${token}` } }
                );
                console.log(`Questions for ${feedbackType} fetched successfully:`, response.data);
                setQuestions(response.data);
            } catch (err) {
                console.error('Error fetching questions:', err);
                setError('Failed to load questions. Please try again.');
            }
        };

        fetchQuestions();
    }, [feedbackType, navigate]);

    useEffect(() => {
        if (!feedbackType) return; // Fetch faculties only after feedbackType is selected

        const fetchFaculties = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    setError('Authentication token is missing. Redirecting to login...');
                    setTimeout(() => navigate('/'), 2000);
                    return;
                }

                const response = await axios.get('http://localhost:5000/api/feedback/faculties', {
                    headers: { Authorization: `Bearer ${token}` },
                });
                console.log('Faculties fetched successfully:', response.data);

                if (response.data.length === 0) {
                    setError('No faculties found for your course and semester.');
                    return;
                }

                // Separate elective and non-elective faculties
                const updatedFaculties = response.data.map((faculty) => ({
                    ...faculty,
                    isElective: electiveFaculties.includes(faculty.facultyName),
                    facultyOptions: electiveFaculties.includes(faculty.facultyName)
                        ? electiveFaculties
                        : [],
                }));

                setFaculties(updatedFaculties);

                // Initialize feedback state for each faculty
                const initialFeedback = {};
                updatedFaculties.forEach((faculty) => {
                    initialFeedback[faculty._id] = Array(questions.length).fill(0);
                });
                setFeedback(initialFeedback);
            } catch (err) {
                console.error('Error fetching faculties:', err);
                setError('Failed to load faculties. Please try again.');
            }
        };

        fetchFaculties();
    }, [feedbackType, questions, navigate, electiveFaculties]);

    const handleFeedbackTypeChange = (type) => {
        setFeedbackType(type);
        setFeedback({});
        setFaculties([]);
        setQuestions([]);
        setSelectedElectiveFaculty({});
        setActiveFaculty(null);
    };

    const handleQuestionChange = (facultyId, questionIndex, value) => {
        const clampedValue = Math.max(1, Math.min(5, parseInt(value))); // Clamp value between 1 and 5
        setFeedback((prev) => {
            const updated = { ...prev };
            updated[facultyId][questionIndex] = clampedValue;
            return updated;
        });
    };

    const handleElectiveFacultyChange = (facultyId, selectedFaculty) => {
        setSelectedElectiveFaculty((prev) => ({
            ...prev,
            [facultyId]: selectedFaculty,
        }));

        // Remove feedback for unselected elective faculties
        setFeedback((prev) => {
            const updatedFeedback = { ...prev };
            if (!selectedFaculty) {
                delete updatedFeedback[facultyId];
            }
            return updatedFeedback;
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        // Validate that all questions are filled with values between 1 and 5
        for (const facultyId in feedback) {
            const scores = feedback[facultyId];
            if (scores.length !== questions.length || scores.some((score) => score < 1 || score > 5 || isNaN(score))) {
                setError('All questions must be filled with values between 1 and 5.');
                setLoading(false);
                return;
            }
        }

        try {
            const token = localStorage.getItem('token');

            // Filter feedback to include only selected elective faculties
            const filteredFeedback = { ...feedback };
            const selectedElectives = Object.keys(selectedElectiveFaculty).filter(
                (facultyId) => selectedElectiveFaculty[facultyId]
            );

            Object.keys(selectedElectiveFaculty).forEach((facultyId) => {
                if (!selectedElectiveFaculty[facultyId]) {
                    delete filteredFeedback[facultyId];
                }
            });

            await axios.post(
                'http://localhost:5000/api/feedback/submit',
                { feedback: filteredFeedback, selectedElectives },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            navigate('/feedback-submitted');
        } catch (err) {
            setError('Failed to submit feedback. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="feedback-container">
            <h2>Faculty Feedback Form</h2>
            {error && <p className="error">{error}</p>}
            <div className="feedback-type-selector">
                <button
                    className={`feedback-type-button ${feedbackType === 'Pre-Feedback' ? 'active' : ''}`}
                    onClick={() => handleFeedbackTypeChange('Pre-Feedback')}
                >
                    Pre-Feedback
                </button>
                <button
                    className={`feedback-type-button ${feedbackType === 'Post-Feedback' ? 'active' : ''}`}
                    onClick={() => handleFeedbackTypeChange('Post-Feedback')}
                >
                    Post-Feedback
                </button>
            </div>
            {feedbackType && (
                <>
                    <div className="faculty-list">
                        {faculties.map((faculty) => (
                            <div
                                key={faculty._id}
                                className={`faculty-box ${activeFaculty === faculty._id ? 'active' : ''}`}
                                onClick={() => setActiveFaculty(faculty._id)}
                            >
                                <h3>{faculty.facultyName}</h3>
                            </div>
                        ))}
                    </div>
                    {activeFaculty && (
                        <div className="faculty-questions">
                            <h3>
                                {faculties.find((faculty) => faculty._id === activeFaculty)?.facultyName}
                            </h3>
                            {questions.map((question, i) => (
                                <div key={i} className="question">
                                    <label>{question}</label>
                                    <input
                                        type="number"
                                        min="1"
                                        max="5"
                                        value={feedback[activeFaculty]?.[i] || ''}
                                        onChange={(e) =>
                                            handleQuestionChange(activeFaculty, i, e.target.value)
                                        }
                                        required
                                    />
                                </div>
                            ))}
                        </div>
                    )}
                    <form onSubmit={handleSubmit}>
                        <button type="submit" disabled={loading}>
                            {loading ? <ClipLoader size={20} color="#fff" /> : 'Submit Feedback'}
                        </button>
                    </form>
                </>
            )}
        </div>
    );
};

export default FeedbackForm;
