import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { ClipLoader } from 'react-spinners';
import './FeedbackForm.css';

const FeedbackForm = () => {
    const [faculties, setFaculties] = useState([]);
    const [feedback, setFeedback] = useState({});
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [activeFaculty, setActiveFaculty] = useState(null); // Track the active faculty tab
    const navigate = useNavigate();

    const questions = [
        "How well does the faculty explain the concepts?",
        "How approachable is the faculty for doubts?",
        "How effective is the faculty's teaching method?",
        "How well does the faculty manage time during lectures?",
        "How engaging are the faculty's lectures?",
        "How clear are the faculty's explanations?",
        "How well does the faculty encourage student participation?",
        "How organized is the faculty in delivering the course content?",
        "How well does the faculty provide feedback on assignments?",
        "How knowledgeable is the faculty about the subject?",
    ];

    useEffect(() => {
        const fetchFaculties = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    setError('Authentication token is missing. Redirecting to login...');
                    setTimeout(() => navigate('/'), 2000); // Redirect to login after 2 seconds
                    return;
                }

                console.log('Fetching faculties with token:', token);
                const response = await axios.get('http://localhost:5000/api/feedback/faculties', {
                    headers: { Authorization: `Bearer ${token}` },
                });
                console.log('Faculties fetched successfully:', response.data);
                setFaculties(response.data);

                // Initialize feedback state for each faculty
                const initialFeedback = {};
                response.data.forEach((faculty) => {
                    initialFeedback[faculty._id] = Array(10).fill(0); // 10 questions
                });
                setFeedback(initialFeedback);

                // Set the first faculty as active by default
                if (response.data.length > 0) {
                    setActiveFaculty(response.data[0]._id);
                }
            } catch (err) {
                console.error('Error fetching faculties:', err);
                if (err.response) {
                    setError(err.response.data?.message || 'Failed to load faculties. Please try again.');
                } else {
                    setError('Failed to load faculties. Please check your connection.');
                }
            }
        };

        fetchFaculties();
    }, [navigate]);

    const handleQuestionChange = (facultyId, questionIndex, value) => {
        setFeedback((prev) => {
            const updated = { ...prev };
            updated[facultyId][questionIndex] = parseInt(value);
            return updated;
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            const token = localStorage.getItem('token');
            await axios.post(
                'http://localhost:5000/api/feedback/submit',
                { feedback },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            navigate('/feedback-submitted');
        } catch (err) {
            setError('Failed to submit feedback. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="feedback-container">
            <h2>Faculty Feedback Form</h2>
            {error && <p className="error">{error}</p>}
            <div className="tabs">
                {faculties.map((faculty) => (
                    <button
                        key={faculty._id}
                        className={`tab-button ${activeFaculty === faculty._id ? 'active' : ''}`}
                        onClick={() => setActiveFaculty(faculty._id)}
                    >
                        {faculty.facultyName}
                    </button>
                ))}
            </div>
            <form onSubmit={handleSubmit}>
                {faculties.map((faculty) =>
                    activeFaculty === faculty._id ? (
                        <div key={faculty._id} className="faculty-panel">
                            <h3>{faculty.facultyName}</h3>
                            {questions.map((question, i) => (
                                <div key={i} className="question">
                                    <label>{question}</label>
                                    <input
                                        type="number"
                                        min="1"
                                        max="5"
                                        value={feedback[faculty._id]?.[i] || 0}
                                        onChange={(e) => handleQuestionChange(faculty._id, i, e.target.value)}
                                        required
                                    />
                                </div>
                            ))}
                        </div>
                    ) : null
                )}
                <button type="submit" disabled={loading}>
                    {loading ? <ClipLoader size={20} color="#fff" /> : 'Submit Feedback'}
                </button>
            </form>
        </div>
    );
};

export default FeedbackForm;
