import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { ClipLoader } from 'react-spinners';
import './FeedbackForm.css';

const FeedbackForm = () => {
    const [faculties, setFaculties] = useState([]);
    const [feedback, setFeedback] = useState({});
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const fetchFaculties = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get('http://localhost:5000/api/faculties', {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setFaculties(response.data);
                // Initialize feedback state for each faculty
                const initialFeedback = {};
                response.data.forEach((faculty) => {
                    initialFeedback[faculty._id] = Array(32).fill(0); // 32 questions
                    initialFeedback[faculty._id].push(0); // Final remark out of 100
                });
                setFeedback(initialFeedback);
            } catch (err) {
                setError('Failed to load faculties. Please try again.');
            }
        };

        fetchFaculties();
    }, []);

    const handleQuestionChange = (facultyId, questionIndex, value) => {
        setFeedback((prev) => {
            const updated = { ...prev };
            updated[facultyId][questionIndex] = parseInt(value);
            return updated;
        });
    };

    const handleRemarkChange = (facultyId, value) => {
        setFeedback((prev) => {
            const updated = { ...prev };
            updated[facultyId][32] = parseInt(value); // Final remark at index 32
            return updated;
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            const token = localStorage.getItem('token');
            await axios.post(
                'http://localhost:5000/api/feedback/submit',
                { feedback },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            navigate('/feedback-submitted');
        } catch (err) {
            setError('Failed to submit feedback. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="feedback-container">
            <h2>Faculty Feedback Form</h2>
            {error && <p className="error">{error}</p>}
            <form onSubmit={handleSubmit}>
                {faculties.map((faculty) => (
                    <div key={faculty._id} className="faculty-section">
                        <h3>{faculty.name}</h3>
                        {Array.from({ length: 32 }, (_, i) => (
                            <div key={i} className="question">
                                <label>Question {i + 1}: Rate from 1 to 5</label>
                                <input
                                    type="number"
                                    min="1"
                                    max="5"
                                    value={feedback[faculty._id]?.[i] || 0}
                                    onChange={(e) => handleQuestionChange(faculty._id, i, e.target.value)}
                                    required
                                />
                            </div>
                        ))}
                        <div className="remark">
                            <label>Final Remark (out of 100)</label>
                            <input
                                type="number"
                                min="0"
                                max="100"
                                value={feedback[faculty._id]?.[32] || 0}
                                onChange={(e) => handleRemarkChange(faculty._id, e.target.value)}
                                required
                            />
                        </div>
                    </div>
                ))}
                <button type="submit" disabled={loading}>
                    {loading ? <ClipLoader size={20} color="#fff" /> : 'Submit Feedback'}
                </button>
            </form>
        </div>
    );
};

export default FeedbackForm;
