import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { ClipLoader } from 'react-spinners';
import './FeedbackForm.css';

const FeedbackForm = () => {
    const [faculties, setFaculties] = useState([]);
    const [feedback, setFeedback] = useState({});
    const [questions, setQuestions] = useState([]);
    const [feedbackType, setFeedbackType] = useState(null); // Initially null
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [studentInfo, setStudentInfo] = useState(null); // New state for student info
    const [academicYear, setAcademicYear] = useState(''); // New state for academic year
    const [semester, setSemester] = useState(''); // New state for semester
    const navigate = useNavigate();

    useEffect(() => {
        const fetchStudentInfo = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    console.error('No token found in localStorage.');
                    setError('Authentication token is missing. Redirecting to login...');
                    setTimeout(() => navigate('/'), 2000);
                    return;
                }

                console.log('Sending token to backend:', token); // Debugging log
                const response = await axios.get('http://localhost:5000/api/student/info', {
                    headers: { Authorization: `Bearer ${token}` },
                });
                console.log('Student Info Response:', response.data); // Debugging log
                setStudentInfo(response.data); // Ensure studentInfo is set correctly
            } catch (err) {
                console.error('Error fetching student info:', err.response?.data || err); // Debugging log
                setError(err.response?.data?.message || 'Failed to load student info. Please try again.');
            }
        };

        fetchStudentInfo();
    }, [navigate]);

    useEffect(() => {
        if (!feedbackType) return; // Fetch questions only after feedbackType is selected

        const fetchQuestions = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    setError('Authentication token is missing. Redirecting to login...');
                    setTimeout(() => navigate('/'), 2000);
                    return;
                }

                const response = await axios.get(
                    `http://localhost:5000/api/feedback/questions/${feedbackType}`,
                    { headers: { Authorization: `Bearer ${token}` } }
                );
                console.log(`Questions for ${feedbackType} fetched successfully:`, response.data);
                setQuestions(response.data);
            } catch (err) {
                console.error('Error fetching questions:', err);
                setError('Failed to load questions. Please try again.');
            }
        };

        fetchQuestions();
    }, [feedbackType, navigate]);

    useEffect(() => {
        if (!feedbackType) return; // Fetch faculties only after feedbackType is selected

        const fetchFaculties = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    setError('Authentication token is missing. Redirecting to login...');
                    setTimeout(() => navigate('/'), 2000);
                    return;
                }

                const response = await axios.get('http://localhost:5000/api/feedback/faculties', {
                    headers: { Authorization: `Bearer ${token}` },
                });
                console.log('Faculties API Response:', response.data);

                if (response.data.length === 0) {
                    setError('No faculties found for your course and semester.');
                    return;
                }

                setFaculties(response.data);

                // Initialize feedback state for each faculty
                const initialFeedback = {};
                response.data.forEach((faculty) => {
                    initialFeedback[faculty._id] = Array(questions.length).fill(0); // Ensure feedback array is initialized
                });
                setFeedback(initialFeedback);
            } catch (err) {
                console.error('Error fetching faculties:', err);
                setError('Failed to load faculties. Please try again.');
            }
        };

        fetchFaculties();
    }, [feedbackType, questions, navigate]);

    const handleFeedbackTypeChange = (type) => {
        if (!academicYear || !semester) {
            setError('Please select both academic year and semester before proceeding.');
            return;
        }
        setFeedbackType(type);
        setFeedback({});
        setFaculties([]);
        setQuestions([]);
            // setActiveFaculty(null); // Removed as it is not defined
        };

    const handleQuestionChange = (facultyId, questionIndex, value) => {
        const clampedValue = Math.max(1, Math.min(5, parseInt(value))); // Clamp value between 1 and 5
        setFeedback((prev) => {
            const updated = { ...prev };
            updated[facultyId][questionIndex] = clampedValue;
            return updated;
        });
    };

    // Removed unused handleElectiveChange function

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        // Validate that all questions are filled with values between 1 and 5
        for (const facultyId in feedback) {
            const scores = feedback[facultyId];
            if (scores.length !== questions.length || scores.some((score) => score < 1 || score > 5 || isNaN(score))) {
                setError('All questions must be filled with values between 1 and 5.');
                setLoading(false);
                return;
            }
        }

        try {
            const token = localStorage.getItem('token');

            await axios.post(
                'http://localhost:5000/api/feedback/submit',
                { feedback },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            navigate('/feedback-submitted');
        } catch (err) {
            setError('Failed to submit feedback. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    const renderFacultyHeaders = () => (
        <thead>
            <tr>
                <th className="question-header">Q No</th>
                <th className="question-header">Question</th>
                {faculties.map((faculty) => (
                    <th key={faculty._id} className="faculty-header">
                        <div className="faculty-box">
                            <p>{faculty.subjectName || 'N/A'}</p> {/* Display subjectName */}
                            <p>{faculty.facultyName || 'N/A'}</p>
                        </div>
                    </th>
                ))}
            </tr>
        </thead>
    );

    const renderQuestions = () => (
        <tbody>
            {questions.map((question, index) => (
                <tr key={index}>
                    <td className="question-number">{index + 1}</td>
                    <td className="question-text">{question}</td>
                    {faculties.map((faculty) => (
                        <td key={faculty._id} className="response-cell">
                            <input
                                type="number"
                                min="1"
                                max="5"
                                value={feedback[faculty._id]?.[index] || ''} // Ensure feedback is initialized
                                onChange={(e) =>
                                    handleQuestionChange(faculty._id, index, e.target.value)
                                }
                                required
                            />
                        </td>
                    ))}
                </tr>
            ))}
        </tbody>
    );

    const renderRatingTable = () => (
        <div className="rating-table">
            <h3>Rating Scale</h3>
            <table>
                <thead>
                    <tr>
                        <th>Rating</th>
                        <th>Description</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>1</td>
                        <td>Poor</td>
                    </tr>
                    <tr>
                        <td>2</td>
                        <td>Fair</td>
                    </tr>
                    <tr>
                        <td>3</td>
                        <td>Good</td>
                    </tr>
                    <tr>
                        <td>4</td>
                        <td>Very Good</td>
                    </tr>
                    <tr>
                        <td>5</td>
                        <td>Excellent</td>
                    </tr>
                </tbody>
            </table>
        </div>
    );

    return (
        <div className="feedback-container">
            <h2>Faculty Feedback Form</h2>
            {error && <p className="error">{error}</p>}

            {studentInfo ? (
                <div className="student-info">
                    <p><strong>Name:</strong> {studentInfo.name}</p>
                    <p><strong>USN:</strong> {studentInfo.studentId}</p>
                    <p><strong>Course:</strong> {studentInfo.course}</p>
                </div>
            ) : (
                <p>Loading student information...</p>
            )}

            <div className="selection-container">
                <div className="dropdown-container">
                    <label className="dropdown-label">
                        Select Academic Year:
                        <select
                            value={academicYear}
                            onChange={(e) => setAcademicYear(e.target.value)}
                            className="styled-dropdown"
                            required
                        >
                            <option value="">--Select Academic Year--</option>
                            <option value="2023-24">2023-24</option>
                        </select>
                    </label>
                </div>
                <div className="dropdown-container">
                    <label className="dropdown-label">
                        Select Semester:
                        <select
                            value={semester}
                            onChange={(e) => setSemester(e.target.value)}
                            className="styled-dropdown"
                            required
                        >
                            <option value="">--Select Semester--</option>
                            <option value="1">1</option>
                            <option value="2">2</option>
                        </select>
                    </label>
                </div>
            </div>

            {renderRatingTable()}

            <div className="feedback-type-selector">
                <button
                    className={`feedback-type-button ${feedbackType === 'Pre-Feedback' ? 'active' : ''}`}
                    onClick={() => handleFeedbackTypeChange('Pre-Feedback')}
                >
                    Pre-Feedback
                </button>
                <button
                    className={`feedback-type-button ${feedbackType === 'Post-Feedback' ? 'active' : ''}`}
                    onClick={() => handleFeedbackTypeChange('Post-Feedback')}
                >
                    Post-Feedback
                </button>
            </div>

            {feedbackType && (
                <form onSubmit={handleSubmit}>
                    <table className="feedback-table">
                        {renderFacultyHeaders()}
                        {renderQuestions()}
                    </table>
                    <button type="submit" disabled={loading}>
                        {loading ? <ClipLoader size={20} color="#fff" /> : 'Submit Feedback'}
                    </button>
                </form>
            )}
        </div>
    );
};

export default FeedbackForm;
