import './FeedbackForm.css';

const FeedbackForm = () => {
    const [faculties, setFaculties] = useState([]);
    const [feedback, setFeedback] = useState({});
    const [questions, setQuestions] = useState([]);
    const [feedbackType, setFeedbackType] = useState(null); // Initially null
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [studentInfo, setStudentInfo] = useState(null); // New state for student info
    const [academicYear, setAcademicYear] = useState(''); // New state for academic year
    const [semester, setSemester] = useState(''); // New state for semester
    const navigate = useNavigate();

    useEffect(() => {
        const fetchStudentInfo = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    console.error('No token found in localStorage.');
                    setError('Authentication token is missing. Redirecting to login...');
                    setTimeout(() => navigate('/'), 2000);
                    return;
                }

                console.log('Sending token to backend:', token); // Debugging log
                const response = await axios.get('http://localhost:5000/api/student/info', {
                    headers: { Authorization: `Bearer ${token}` },
                });
                console.log('Student Info Response:', response.data); // Debugging log
                setStudentInfo(response.data); // Ensure studentInfo is set correctly
            } catch (err) {
                console.error('Error fetching student info:', err.response?.data || err); // Debugging log
                setError(err.response?.data?.message || 'Failed to load student info. Please try again.');
            }
        };

        fetchStudentInfo();
    }, [navigate]);

    useEffect(() => {
        if (!feedbackType) return; // Fetch questions only after feedbackType is selected

        const fetchQuestions = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    setError('Authentication token is missing. Redirecting to login...');
                    setTimeout(() => navigate('/'), 2000);
                    return;
                }

                const response = await axios.get(
                    `http://localhost:5000/api/feedback/questions/${feedbackType}`,
                    { headers: { Authorization: `Bearer ${token}` } }
                );
                console.log(`Questions for ${feedbackType} fetched successfully:`, response.data);
                setQuestions(response.data);
            } catch (err) {
                console.error('Error fetching questions:', err);
                setError('Failed to load questions. Please try again.');
            }
        };

        fetchQuestions();
    }, [feedbackType, navigate]);

    useEffect(() => {
        if (!feedbackType) return; // Fetch faculties only after feedbackType is selected

        const fetchFaculties = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    setError('Authentication token is missing. Redirecting to login...');
                    setTimeout(() => navigate('/'), 2000);
                    return;
                }

                const response = await axios.get('http://localhost:5000/api/feedback/faculties', {
                    headers: { Authorization: `Bearer ${token}` },
                });
                console.log('Faculties API Response:', response.data);

                if (response.data.length === 0) {
                    setError('No faculties found for your course and semester.');
                    return;
                }

                setFaculties(response.data);

                // Initialize feedback state for each faculty
                const initialFeedback = {};
                response.data.forEach((faculty) => {
                    initialFeedback[faculty._id] = {
                        scores: Array(26).fill(0), // Initialize scores with 0
                        comment: '', // Initialize comment as empty
                    };
                });
                console.log('Initialized feedback state:', initialFeedback);
                setFeedback(initialFeedback);
            } catch (err) {
                console.error('Error fetching faculties:', err);
                setError('Failed to load faculties. Please try again.');
            }
        };

        fetchFaculties();
    }, [feedbackType, questions, navigate]);

    const handleFeedbackTypeChange = (type) => {
        if (!academicYear || !semester) {
            setError('Please select both academic year and semester before proceeding.');
            return;
        }
        setFeedbackType(type);
        setFeedback({});
        setFaculties([]);
        setQuestions([]);
    };

    const handleQuestionChange = (facultyId, questionIndex, value) => {
        const clampedValue = Math.max(1, Math.min(5, parseInt(value))); // Clamp value between 1 and 5
        setFeedback((prev) => {
            const updated = { ...prev };
            updated[facultyId].scores[questionIndex] = clampedValue;
            return updated;
        });
    };

    const handleElectiveChange = (facultyId, selectedFaculty) => {
        setFeedback((prev) => ({
            ...prev,
            [facultyId]: {
                ...prev[facultyId],
                selectedFaculty,
            },
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            const token = localStorage.getItem('token');
            if (!token) {
                throw new Error('Authentication token missing');
            }

            // Transform feedback data with proper ID handling
            const feedbackPayload = Object.entries(feedback).map(([facultyId, data]) => {
                const faculty = faculties.find(f => f._id === facultyId);
                console.log('Processing faculty:', { facultyId, faculty });
                
                if (!data.scores || data.scores.some(score => !score)) {
                    throw new Error('Please provide scores for all questions');
                }

                // Extract numeric ID from MongoDB ObjectId or string
                const numericId = parseInt(facultyId.replace(/.*?(\d+)$/, '$1'), 10);
                console.log('Converted faculty ID:', { original: facultyId, numeric: numericId });

                if (isNaN(numericId)) {
                    throw new Error(`Invalid faculty ID format: ${facultyId}`);
                }

                return {
                    facultyId: numericId,
                    scores: data.scores.map(score => parseInt(score, 10)),
                    selectedFaculty: faculty?.isElective ? data.selectedFaculty : faculty?.facultyName,
                    comment: data.comment || ''
                };
            });

            console.log('Submitting feedback:', feedbackPayload);

            const response = await axios.post(
                'http://localhost:5000/api/feedback/submit',
                { feedback: feedbackPayload },
                {
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json'
                    }
                }
            );

            if (response.data.success) {
                localStorage.setItem(`feedbackSubmitted_${studentInfo.studentId}`, 'true');
                navigate('/feedback-submitted');
            } else {
                throw new Error(response.data.message);
            }
        } catch (err) {
            console.error('Feedback submission error:', err);
            setError(err.response?.data?.message || err.message || 'Failed to submit feedback');
        } finally {
            setLoading(false);
        }
    };

    const renderFacultyHeaders = () => (
        <thead>
            <tr>
                <th className="question-header">Q No</th>
                <th className="question-header">Question</th>
                {faculties.map((faculty) => (
                    <th key={faculty._id} className="faculty-header"> {/* Use faculty._id as the key */}
                        <div className="faculty-box">
                            <p>{faculty.subjectName || 'N/A'}</p>
                            {faculty.isElective ? (
                                <select
                                    className="elective-dropdown"
                                    onChange={(e) => handleElectiveChange(faculty._id, e.target.value)}
                                    value={feedback[faculty._id]?.selectedFaculty || ''}
                                >
                                    <option value="">Select Faculty</option>
                                    {faculty.facultyName.split('/').map((name, index) => (
                                        <option key={`${faculty._id}-${index}`} value={name.trim()}> {/* Use a combination of faculty._id and index */}
                                            {name.trim()}
                                        </option>
                                    ))}
                                </select>
                            ) : (
                                <p>{faculty.facultyName || 'N/A'}</p>
                            )}
                        </div>
                    </th>
                ))}
            </tr>
        </thead>
    );

    const renderQuestions = () => (
        <tbody>
            {questions.map((question, index) => (
                <tr key={index}>
                    <td className="question-number">{index + 1}</td>
                    <td className="question-text">{question}</td>
                    {faculties.map((faculty) => (
                        <td key={`${faculty._id}-${index}`} className="response-cell"> {/* Use a combination of faculty._id and index */}
                            <input
                                type="number"
                                min="1"
                                max="5"
                                value={feedback[faculty._id]?.scores[index] || ''} // Ensure feedback is initialized
                                onChange={(e) =>
                                    handleQuestionChange(faculty._id, index, e.target.value)
                                }
                                required
                            />
                        </td>
                    ))}
                </tr>
            ))}
            {/* Add a row for comments */}
            <tr>
                <td className="question-number">{questions.length + 1}</td>
                <td className="question-text">Comment</td>
                {faculties.map((faculty) => (
                    <td key={`${faculty._id}-comment`} className="response-cell"> {/* Use faculty._id with a suffix */}
                        <textarea
                            className="comment-box"
                            value={feedback[faculty._id]?.comment || ''} // Ensure comment is initialized
                            onChange={(e) =>
                                setFeedback((prev) => ({
                                    ...prev,
                                    [faculty._id]: {
                                        ...prev[faculty._id],
                                        comment: e.target.value,
                                    },
                                }))
                            }
                            placeholder="Enter your comment here"
                        />
                    </td>
                ))}
            </tr>
        </tbody>
    );

    return (
        <div className="feedback-container">
            <h2>Faculty Feedback Form</h2>
            {error && <p className="error">{error}</p>}

            {/* Student details section */}
            {studentInfo ? (
                <div className="student-info">
                    <p><strong>Name:</strong> {studentInfo.name}</p>
                    <p><strong>USN:</strong> {studentInfo.studentId}</p>
                    <p><strong>Course:</strong> {studentInfo.course.toUpperCase()}</p>
                </div>
            ) : (
                <p>Loading student information...</p>
            )

            }

            {/* Feedback table */}
            <table className="feedback-table">
                <thead>
                    <tr>
                        <th>Poor</th>
                        <th>Fair</th>
                        <th>Average</th>
                        <th>Good</th>
                        <th>Excellent</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>1</td>
                        <td>2</td>
                        <td>3</td>
                        <td>4</td>
                        <td>5</td>
                    </tr>
                </tbody>
            </table>

            <div className="selection-container">
                <div className="dropdown-container">
                    <label className="dropdown-label">
                        Select Academic Year:
                        <select
                            value={academicYear}
                            onChange={(e) => setAcademicYear(e.target.value)}
                            className="styled-dropdown"
                            required
                        >
                            <option value="">--Select Academic Year--</option>
                            <option value="2023-24">2023-24</option>
                        </select>
                    </label>
                </div>
                <div className="dropdown-container">
                    <label className="dropdown-label">
                        Select Semester:
                        <select
                            value={semester}
                            onChange={(e) => setSemester(e.target.value)}
                            className="styled-dropdown"
                            required
                        >
                            <option value="">--Select Semester--</option>
                            <option value="1">1</option>
                            <option value="2">2</option>
                        </select>
                    </label>
                </div>
            </div>

            <div className="feedback-type-container">
                <h3>Select Feedback Type</h3>
                <div className="feedback-type-selector">
                    <button
                        className={`feedback-type-button ${feedbackType === 'Pre-Feedback' ? 'active pre' : ''}`}
                        onClick={() => handleFeedbackTypeChange('Pre-Feedback')}
                        disabled={!academicYear || !semester}
                    >
                        Pre-Feedback
                    </button>
                    <button
                        className={`feedback-type-button ${feedbackType === 'Post-Feedback' ? 'active post' : ''}`}
                        onClick={() => handleFeedbackTypeChange('Post-Feedback')}
                        disabled={!academicYear || !semester}
                    >
                        Post-Feedback
                    </button>
                </div>
            </div>

            {feedbackType && (
                <form onSubmit={handleSubmit}>
                    <table className="feedback-table">
                        {renderFacultyHeaders()}
                        {renderQuestions()}
                    </table>
                    <button type="submit" disabled={loading}>
                        {loading ? <ClipLoader size={20} color="#fff" /> : 'Submit Feedback'}
                    </button>
                </form>
            )}
        </div>
    );
};

export default FeedbackForm;
