import React, { useState, useEffect } from 'react';
import axios from 'axios';

const StudentReports = () => {
    const [students, setStudents] = useState([]);
    const [courses, setCourses] = useState([]);
    const [semesters, setSemesters] = useState([]);
    const [filters, setFilters] = useState({
        course: '',
        semester: '',
    });
    const [error, setError] = useState('');

    const handleFilterChange = (e) => {
        const { name, value } = e.target;
        setFilters((prevFilters) => ({
            ...prevFilters,
            [name]: value,
        }));
    };

    useEffect(() => {
        const fetchCoursesAndSemesters = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get('http://localhost:5000/api/courses', {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setCourses([...new Set(response.data.map((course) => course.courseName))]);
                setSemesters([...new Set(response.data.map((course) => course.semester))]);
            } catch (err) {
                console.error('Error fetching courses and semesters:', err);
                setError('Failed to fetch courses and semesters.');
            }
        };

        fetchCoursesAndSemesters();
    }, []);

    useEffect(() => {
        const fetchStudents = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get('http://localhost:5000/api/students', {
                    params: { course: filters.course, semester: filters.semester },
                    headers: { Authorization: `Bearer ${token}` },
                });
                setStudents(response.data);
                setError('');
            } catch (err) {
                console.error('Error fetching students:', err);
                setError(err.response?.data?.message || 'Failed to fetch student data.');
            }
        };

        if (filters.course && filters.semester) {
            fetchStudents();
        }
    }, [filters.course, filters.semester]);

    const downloadStudentReport = async (studentId) => {
        try {
            console.log('Downloading report for studentId:', studentId); // Debugging log
            const token = localStorage.getItem('token'); // Retrieve the token from localStorage
            const response = await axios.get(`http://localhost:5000/api/student-report/${studentId}`, {
                responseType: 'blob', // Ensure the response is treated as a file
                headers: { Authorization: `Bearer ${token}` }, // Include the token in the request headers
            });

            // Create a URL for the blob and trigger the download
            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download', `student_report_${studentId}.pdf`); // Set the filename
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link); // Clean up the DOM
        } catch (err) {
            console.error('Error downloading report:', err);

            // Display a meaningful error message
            if (err.response?.status === 404) {
                alert('Student report not found. Please check the student ID.');
            } else if (err.response?.status === 400) {
                alert('Invalid request. Please ensure all required fields are filled.');
            } else {
                alert('Failed to download student report. Please try again later.');
            }
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <h1>Student Reports</h1>
            {error && <p style={{ color: 'red' }}>{error}</p>}

            {/* Filters Section */}
            <div style={{ marginBottom: '20px' }}>
                <label>
                    Course:
                    <select
                        name="course"
                        value={filters.course}
                        onChange={handleFilterChange}
                        style={{ marginLeft: '10px', marginRight: '20px' }}
                    >
                        <option value="">--Select Course--</option>
                        {courses.map((course, index) => (
                            <option key={index} value={course}>
                                {course}
                            </option>
                        ))}
                    </select>
                </label>
                <label>
                    Semester:
                    <select
                        name="semester"
                        value={filters.semester}
                        onChange={handleFilterChange}
                        style={{ marginLeft: '10px' }}
                    >
                        <option value="">--Select Semester--</option>
                        {semesters.map((semester, index) => (
                            <option key={index} value={semester}>
                                {semester}
                            </option>
                        ))}
                    </select>
                </label>
            </div>

            {/* Students Table */}
            <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px' }}>
                <thead>
                    <tr>
                        <th style={{ border: '1px solid #ddd', padding: '8px' }}>Student ID</th>
                        <th style={{ border: '1px solid #ddd', padding: '8px' }}>Student Name</th>
                        <th style={{ border: '1px solid #ddd', padding: '8px' }}>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {students.map((student) => (
                        <tr key={student.studentId}>
                            <td style={{ border: '1px solid #ddd', padding: '8px' }}>{student.studentId}</td>
                            <td style={{ border: '1px solid #ddd', padding: '8px' }}>{student.studentName}</td>
                            <td style={{ border: '1px solid #ddd', padding: '8px' }}>
                                <button
                                    onClick={() => downloadStudentReport(student.studentId)}
                                    style={{
                                        padding: '5px 10px',
                                        backgroundColor: '#007bff',
                                        color: '#fff',
                                        border: 'none',
                                        borderRadius: '3px',
                                        cursor: 'pointer',
                                    }}
                                >
                                    Download Report
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default StudentReports;
