import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import jsPDF from 'jspdf'; // Import jsPDF for PDF generation
import './Admin.css';

const Admin = () => {
    const navigate = useNavigate();

    const handleLogout = () => {
        localStorage.removeItem('token');
        navigate('/');
    };

    const downloadFacultyReports = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get('http://localhost:5000/api/feedback', {
                headers: { Authorization: `Bearer ${token}` },
            });

            response.data.forEach((faculty) => {
                const doc = new jsPDF();
                doc.setFontSize(16);
                doc.text(`Faculty Report: ${faculty.facultyName}`, 10, 10);
                doc.setFontSize(12);
                doc.text(`Cumulative Average Marks: ${faculty.averageScore.toFixed(2)} / 100`, 10, 20);

                faculty.scores.forEach((score, index) => {
                    doc.text(`Q${index + 1}: ${score}`, 10, 30 + index * 10);
                });

                doc.save(`${faculty.facultyName}_Report.pdf`);
            });
        } catch (err) {
            console.error('Error generating reports:', err);
            alert('Failed to generate reports. Please try again.');
        }
    };

    return (
        <div className="admin-container">
            <h2>Admin Dashboard</h2>
            <div className="admin-options">
                <Link to="/admin/add-courses" className="admin-button">Add Courses</Link>
                <Link to="/admin/add-students" className="admin-button">Add Students</Link>
                <Link to="/admin/feedback-viewer" className="admin-button">View Feedback</Link>
                <button onClick={downloadFacultyReports} className="admin-button">Download Faculty Reports</button>
                <button onClick={handleLogout} className="logout-button">Logout</button>
            </div>
        </div>
    );
};

export default Admin;
