import React, { useState } from 'react';
import axios from 'axios';
import * as XLSX from 'xlsx';
import './Admin.css';

const Admin = () => {
    const [faculties, setFaculties] = useState([]);
    const [academicYear, setAcademicYear] = useState('');
    const [course, setCourse] = useState('');
    const [semester, setSemester] = useState('');
    const [feedbackType, setFeedbackType] = useState('');
    const [error, setError] = useState('');

    const fetchFaculties = async () => {
        try {
            if (!academicYear || !course || !semester || !feedbackType) {
                setError('Please select all filters before fetching data');
                return;
            }

            const token = localStorage.getItem('token');
            if (!token) {
                setError('Authentication token is missing. Please log in again.');
                return;
            }

            console.log('Fetching faculties with params:', { academicYear, course, semester, feedbackType });

            const response = await axios.get('http://localhost:5000/api/feedback/admin/faculties', {
                headers: { Authorization: `Bearer ${token}` },
                params: { 
                    academicYear: academicYear,
                    course: course.toLowerCase(),
                    semester: parseInt(semester),
                    feedbackType: feedbackType
                }
            });

            // Add detailed logging to match PDF data structure
            response.data.forEach(faculty => {
                if (faculty.isElective) {
                    console.log('Elective Faculty Data:', {
                        subject: faculty.subjectName,
                        first: {
                            name: faculty.firstFaculty?.name,
                            students: faculty.firstFaculty?.students,
                            percentile: faculty.firstFaculty?.percentile,
                            score: faculty.firstFaculty?.score,
                        },
                        second: {
                            name: faculty.secondFaculty?.name,
                            students: faculty.secondFaculty?.students,
                            percentile: faculty.secondFaculty?.percentile,
                            score: faculty.secondFaculty?.score,
                        }
                    });
                } else {
                    console.log('Regular Faculty Data:', {
                        subject: faculty.subjectName,
                        name: faculty.facultyName,
                        students: faculty.totalStudents,
                        percentile: faculty.percentileScore,
                        score: faculty.regularScore
                    });
                }
            });

            setFaculties(response.data);
            setError('');
        } catch (err) {
            console.error('Error fetching faculties:', err);
            setError(err.response?.data?.message || 'Failed to load faculties. Please try again.');
            setFaculties([]);
        }
    };

    const handleDownload = async (facultyId, electiveFaculty = null) => {
        try {
            const token = localStorage.getItem('token');
            const url = electiveFaculty ? 
                `http://localhost:5000/api/feedback/generate-pdf/${facultyId}/${encodeURIComponent(electiveFaculty)}` :
                `http://localhost:5000/api/feedback/generate-pdf/${facultyId}`;

            const response = await axios.get(url, {
                responseType: 'blob',
                headers: { Authorization: `Bearer ${token}` },
            });

            const downloadUrl = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = downloadUrl;
            link.setAttribute('download', `feedback-report-${facultyId}${electiveFaculty ? '-' + electiveFaculty : ''}.pdf`);
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);
        } catch (err) {
            console.error('Error downloading PDF:', err);
            setError('Failed to download PDF report.');
        }
    };

    const downloadExcel = async (facultyId, electiveFaculty = null) => {
        try {
            const token = localStorage.getItem('token');
            const url = electiveFaculty ? 
                `http://localhost:5000/api/feedback/faculty-feedback-excel/${facultyId}/${encodeURIComponent(electiveFaculty)}` :
                `http://localhost:5000/api/feedback/faculty-feedback-excel/${facultyId}`;

            const response = await axios.get(url, {
                headers: { Authorization: `Bearer ${token}` },
            });

            const data = response.data;
            const ws = XLSX.utils.json_to_sheet(data);
            const wb = XLSX.utils.book_new();
            XLSX.utils.book_append_sheet(wb, ws, 'Feedback Data');
            XLSX.writeFile(wb, `faculty_feedback_${facultyId}${electiveFaculty ? '_' + electiveFaculty : ''}.xlsx`);
        } catch (err) {
            console.error('Error downloading Excel:', err);
            setError('Failed to download Excel report.');
        }
    };

    return (
        <div className="admin-container">
            <h2>Admin Dashboard</h2>
            {error && <p className="error">{error}</p>}
            <div className="filters">
                <select value={academicYear} onChange={(e) => setAcademicYear(e.target.value)}>
                    <option value="">Select Academic Year</option>
                    <option value="2023-24">2023-24</option>
                </select>
                <select value={course} onChange={(e) => setCourse(e.target.value)}>
                    <option value="">Select Course</option>
                    <option value="b.tech cse">B.Tech CSE</option>
                    <option value="b.tech aiml">B.Tech AIML</option>
                </select>
                <select value={semester} onChange={(e) => setSemester(e.target.value)}>
                    <option value="">Select Semester</option>
                    <option value="1">1</option>
                    <option value="2">2</option>
                </select>
                <select value={feedbackType} onChange={(e) => setFeedbackType(e.target.value)}>
                    <option value="">Select Feedback Type</option>
                    <option value="Pre-Feedback">Pre-Feedback</option>
                    <option value="Post-Feedback">Post-Feedback</option>
                </select>
                <button onClick={fetchFaculties}>Fetch Faculties</button>
            </div>

            {faculties.length > 0 && (
                <table>
                    <thead>
                        <tr>
                            <th>Subject Name</th>
                            <th>Faculty</th>
                            <th>Students</th>
                            <th>Average Score</th>
                            <th>Percentile</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {faculties.map((faculty) => 
                            faculty.isElective ? (
                                <React.Fragment key={faculty._id}>
                                    <tr>
                                        <td rowSpan="2">{faculty.subjectName}</td>
                                        <td>{faculty.firstFaculty?.name || 'N/A'}</td>
                                        <td>{faculty.firstFaculty?.students || 0}</td>
                                        <td>{faculty.firstFaculty?.score ? Number(faculty.firstFaculty.score).toFixed(2) : 'N/A'}</td>
                                        <td>{faculty.firstFaculty?.percentile ? `${Number(faculty.firstFaculty.percentile).toFixed(2)}%` : 'N/A'}</td>
                                        <td>
                                            <button onClick={() => handleDownload(faculty._id, faculty.firstFaculty?.name)}>Download PDF</button>
                                            <button onClick={() => downloadExcel(faculty._id, faculty.firstFaculty?.name)}>Download Excel</button>
                                        </td>
                                    </tr>
                                    <tr>
                                        <td>{faculty.secondFaculty?.name || 'N/A'}</td>
                                        <td>{faculty.secondFaculty?.students || 0}</td>
                                        <td>{faculty.secondFaculty?.score ? Number(faculty.secondFaculty.score).toFixed(2) : 'N/A'}</td>
                                        <td>{faculty.secondFaculty?.percentile ? `${Number(faculty.secondFaculty.percentile).toFixed(2)}%` : 'N/A'}</td>
                                        <td>
                                            <button onClick={() => handleDownload(faculty._id, faculty.secondFaculty?.name)}>Download PDF</button>
                                            <button onClick={() => downloadExcel(faculty._id, faculty.secondFaculty?.name)}>Download Excel</button>
                                        </td>
                                    </tr>
                                </React.Fragment>
                            ) : (
                                <tr key={faculty._id}>
                                    <td>{faculty.subjectName}</td>
                                    <td>{faculty.facultyName}</td>
                                    <td>{faculty.totalStudents || 0}</td>
                                    <td>{faculty.regularScore ? Number(faculty.regularScore).toFixed(2) : 'N/A'}</td>
                                    <td>{faculty.percentileScore ? `${Number(faculty.percentileScore).toFixed(2)}%` : 'N/A'}</td>
                                    <td>
                                        <button onClick={() => handleDownload(faculty._id)}>Download PDF</button>
                                        <button onClick={() => downloadExcel(faculty._id)}>Download Excel</button>
                                    </td>
                                </tr>
                            )
                        )}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default Admin;
