import React, { useEffect, useState } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Header from './components/Header/Header';
import FeedbackLogin from './components/Login/FeedbackLogin';
import FeedbackForm from './components/Feedback/FeedbackForm';
import FeedbackSubmitted from './components/FeedbackSubmitted/FeedbackSubmitted';
import Admin from './components/Admin/Admin';
import AddCourseData from './components/Admin/AddCourseData';
import AddStudentData from './components/Admin/AddStudentData';
import FeedbackViewer from './components/Admin/FeedbackViewer';
import './App.css';

function App() {
    const [footerVisible, setFooterVisible] = useState(false);

    useEffect(() => {
        const handleScroll = () => {
            const scrollTop = window.scrollY;
            const windowHeight = window.innerHeight;
            const documentHeight = document.documentElement.scrollHeight;

            // Show footers only when scrolled near the bottom
            if (scrollTop + windowHeight >= documentHeight - 50) {
                setFooterVisible(true);
            } else {
                setFooterVisible(false);
            }
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    return (
        <BrowserRouter>
            <div className="app-container">
                <Routes>
                    <Route path="/" element={<FeedbackLogin />} />
                    <Route
                        path="/feedback"
                        element={
                            <>
                                <Header />
                                <FeedbackForm />
                            </>
                        }
                    />
                    <Route
                        path="/feedback-submitted"
                        element={
                            <>
                                <Header />
                                <FeedbackSubmitted />
                            </>
                        }
                    />
                    <Route
                        path="/admin"
                        element={
                            <>
                                <Header />
                                <Admin />
                            </>
                        }
                    />
                    <Route
                        path="/admin/add-courses"
                        element={
                            <>
                                <Header />
                                <AddCourseData />
                            </>
                        }
                    />
                    <Route
                        path="/admin/add-students"
                        element={
                            <>
                                <Header />
                                <AddStudentData />
                            </>
                        }
                    />
                    <Route
                        path="/admin/feedback-viewer"
                        element={
                            <>
                                <Header />
                                <FeedbackViewer />
                            </>
                        }
                    />
                    <Route
                        path="*"
                        element={
                            <>
                                <Header />
                                <div>404 - Page Not Found</div>
                            </>
                        }
                    />
                </Routes>
                <footer className={`footer ${footerVisible ? 'visible' : ''}`}>
                    <div className="footer-left">Designed by: Sachin © | Sharon Melvin | Aditya Banerjee</div>
                    <div className="footer-right">Powered by Amity University Bengaluru</div>
                </footer>
            </div>
        </BrowserRouter>
    );
}

export default App;
