import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { ClipLoader } from 'react-spinners';
import amityLogo from "D:\Desktop\FeedBack\frontend\src\assets\amity-logo.png";
import './FeedbackLogin.css';

const FeedbackLogin = () => {
    const [studentId, setStudentId] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            const response = await axios.post('http://localhost:5000/api/auth/login', {
                studentId,
                password,
            });

            if (response.data.role === 'student') {
                localStorage.setItem('token', response.data.token);
                navigate('/feedback'); // Redirect to FeedbackForm
            } else if (response.data.role === 'admin') {
                localStorage.setItem('token', response.data.token);
                navigate('/admin'); // Redirect to Admin page
            }
        } catch (err) {
            setError('Invalid credentials. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="login-container">
            <div className="login-box">
                <img src={amityLogo} alt="Amity University Logo" className="logo" />
                <h2>Student Login</h2>
                <form onSubmit={handleLogin}>
                    <div className="form-group">
                        <label htmlFor="studentId">Student ID</label>
                        <input
                            type="text"
                            id="studentId"
                            value={studentId}
                            onChange={(e) => setStudentId(e.target.value)}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Password</label>
                        <input
                            type="password"
                            id="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                    {error && <p className="error">{error}</p>}
                    <button type="submit" disabled={loading}>
                        {loading ? <ClipLoader size={20} color="#fff" /> : 'Login'}
                    </button>
                </form>
            </div>
        </div>
    );
};

export default FeedbackLogin;
