import React, { useState } from 'react';
import axios from 'axios';
import * as XLSX from 'xlsx';
import './Admin.css';

const Admin = () => {
    const [faculties, setFaculties] = useState([]);
    const [academicYear, setAcademicYear] = useState('');
    const [course, setCourse] = useState('');
    const [semester, setSemester] = useState('');
    const [feedbackType, setFeedbackType] = useState('');
    const [error, setError] = useState('');

    const fetchFaculties = async () => {
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                setError('Authentication token is missing. Please log in again.');
                console.error('Token is missing in localStorage.'); // Debugging log
                return;
            }

            console.log('Sending token in Authorization header:', token); // Debugging log
            const response = await axios.get('http://localhost:5000/api/feedback/admin/faculties', {
                headers: { Authorization: `Bearer ${token}` },
                params: { academicYear, course, semester, feedbackType },
            });
            setFaculties(response.data);
            setError(''); // Clear any previous errors
        } catch (err) {
            console.error('Error fetching faculties:', err.response || err); // Debugging log
            setError(err.response?.data?.message || 'Failed to load faculties.');
        }
    };

    const handleDownload = async (facultyId) => {
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                setError('Authentication token is missing. Please log in again.');
                console.error('Token is missing in localStorage.'); // Debugging log
                return;
            }

            console.log('Sending token in Authorization header for download:', token); // Debugging log
            const response = await axios.get(`http://localhost:5000/api/feedback/generate-pdf/${facultyId}`, {
                responseType: 'blob',
                headers: { Authorization: `Bearer ${token}` },
            });
            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download', `feedback-report-${facultyId}.pdf`);
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);
        } catch (err) {
            console.error('Error downloading PDF:', err.response || err); // Debugging log
            setError('Failed to download PDF report.');
        }
    };

    const downloadExcel = async (facultyId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get(`http://localhost:5000/api/feedback/faculty-feedback-excel/${facultyId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });

            const data = response.data;
            const ws = XLSX.utils.json_to_sheet(data);
            const wb = XLSX.utils.book_new();
            XLSX.utils.book_append_sheet(wb, ws, 'Feedback Data');
            XLSX.writeFile(wb, `faculty_feedback_${facultyId}.xlsx`);
        } catch (err) {
            console.error('Error downloading Excel:', err.response || err); // Debugging log
            setError('Failed to download Excel report.');
        }
    };

    return (
        <div className="admin-container">
            <h2>Admin Dashboard</h2>
            {error && <p className="error">{error}</p>}
            <div className="filters">
                <select value={academicYear} onChange={(e) => setAcademicYear(e.target.value)}>
                    <option value="">Select Academic Year</option>
                    <option value="2023-24">2023-24</option>
                </select>
                <select value={course} onChange={(e) => setCourse(e.target.value)}>
                    <option value="">Select Course</option>
                    <option value="b.tech cse">B.Tech CSE</option>
                    <option value="b.tech aiml">B.Tech AIML</option>
                </select>
                <select value={semester} onChange={(e) => setSemester(e.target.value)}>
                    <option value="">Select Semester</option>
                    <option value="1">1</option>
                    <option value="2">2</option>
                </select>
                <select value={feedbackType} onChange={(e) => setFeedbackType(e.target.value)}>
                    <option value="">Select Feedback Type</option>
                    <option value="Pre-Feedback">Pre-Feedback</option>
                    <option value="Post-Feedback">Post-Feedback</option>
                </select>
                <button onClick={fetchFaculties}>Fetch Faculties</button>
            </div>

            {faculties.length > 0 && (
                <table>
                    <thead>
                        <tr>
                            <th>Faculty Name</th>
                            <th>Subject Name</th>
                            <th>Percentile Score</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {faculties.map((faculty, index) => (
                            <tr key={`${faculty._id}-${index}`}> {/* Ensure unique key */}
                                <td>{faculty.facultyName}</td>
                                <td>{faculty.subjectName}</td>
                                <td>{faculty.percentileScore !== 'N/A' ? `${faculty.percentileScore}%` : 'N/A'}</td> {/* Display percentileScore */}
                                <td>
                                    <button onClick={() => handleDownload(faculty._id)}>Download PDF</button>
                                    <button onClick={() => downloadExcel(faculty._id)}>Download Excel</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default Admin;
